PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE ENVIO(CodGrupo CHAR(15) NOT NULL, CodUsuario CHAR(15), CodPrestamo VARCHAR(25), CodPago CHAR(2) NOT NULL, MontoPrestamo MONEY(13, 7), MontoGarantia MONEY(13, 7), Observaciones VARCHAR(100), FechReg DATETIME, FechUpd DATETIME, Exported INTEGER DEFAULT 0);
CREATE VIEW [vEnvio] AS 
select CodGrupo, CodPrestamo, sum (montoprestamo + montogarantia) as Total from ENVIO Group by CodGrupo;
CREATE TRIGGER T_LISTAGPOS_FECHUPD AFTER UPDATE ON ENVIO BEGIN UPDATE ENVIO SET FECHUPD = datetime('now','localtime') WHERE rowid = new.rowid; END;
CREATE TRIGGER T_LISTAGPOS_FECHREG AFTER INSERT ON ENVIO BEGIN UPDATE ENVIO SET FECHREG = datetime('now','localtime') WHERE rowid = new.rowid; END;
COMMIT;
